import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { mockCourses, mockAssignments, mockAnnouncements, mockAttendance, mockGrades } from '../data/mockData';
import { 
  AcademicCapIcon, 
  CalendarIcon, 
  ChartBarIcon, 
  ClockIcon, 
  DocumentTextIcon, 
  UserGroupIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  BellIcon,
  BookOpenIcon
} from '@heroicons/react/24/outline';

export default function Dashboard() {
  const { user } = useAuth();

  const getStudentStats = () => {
    const totalCourses = mockCourses.length;
    const avgGrade = mockGrades.reduce((sum, grade) => sum + grade.percentage, 0) / mockGrades.length;
    const attendancePercentage = (mockAttendance.filter(a => a.status === 'present').length / mockAttendance.length) * 100;
    const upcomingAssignments = mockAssignments.filter(a => new Date(a.dueDate) > new Date()).length;

    return [
      { name: 'Current GPA', value: (avgGrade / 25).toFixed(1), change: '+0.2', changeType: 'positive', icon: ChartBarIcon },
      { name: 'Enrolled Courses', value: totalCourses.toString(), change: '0', changeType: 'neutral', icon: AcademicCapIcon },
      { name: 'Attendance', value: `${Math.round(attendancePercentage)}%`, change: '+5%', changeType: 'positive', icon: CheckCircleIcon },
      { name: 'Pending Assignments', value: upcomingAssignments.toString(), change: '-1', changeType: 'negative', icon: DocumentTextIcon },
    ];
  };

  const getTeacherStats = () => {
    const totalCourses = mockCourses.length;
    const totalStudents = mockCourses.reduce((sum, course) => sum + course.enrolledStudents, 0);
    const pendingGrading = mockAssignments.length * 3; // Demo calculation
    const avgAttendance = 87; // Demo value

    return [
      { name: 'Total Courses', value: totalCourses.toString(), change: '0', changeType: 'neutral', icon: AcademicCapIcon },
      { name: 'Total Students', value: totalStudents.toString(), change: '+5', changeType: 'positive', icon: UserGroupIcon },
      { name: 'Pending Grading', value: pendingGrading.toString(), change: '-3', changeType: 'negative', icon: DocumentTextIcon },
      { name: 'Avg Attendance', value: `${avgAttendance}%`, change: '+2%', changeType: 'positive', icon: CheckCircleIcon },
    ];
  };

  const stats = user?.role === 'student' ? getStudentStats() : getTeacherStats();

  const upcomingClasses = [
    {
      id: 1,
      name: 'Web Development',
      time: '09:00 AM - 10:30 AM',
      location: 'Building A, Room 302',
      instructor: 'Dr. Smith',
      type: 'lecture'
    },
    {
      id: 2,
      name: 'Database Systems',
      time: '11:00 AM - 12:30 PM',
      location: 'Building B, Room 205',
      instructor: 'Prof. Johnson',
      type: 'lecture'
    },
    {
      id: 3,
      name: 'Web Dev Lab',
      time: '02:00 PM - 04:00 PM',
      location: 'Computer Lab 3',
      instructor: 'Dr. Smith',
      type: 'lab'
    }
  ];

  const quickLinks = user?.role === 'student' 
    ? [
        { name: 'Submit Assignment', href: '/assignments', icon: DocumentTextIcon },
        { name: 'View Grades', href: '/grades', icon: ChartBarIcon },
        { name: 'Course Materials', href: '/resources', icon: BookOpenIcon },
        { name: 'Messages', href: '/messages', icon: BellIcon },
      ]
    : [
        { name: 'Create Assignment', href: '/assignments', icon: DocumentTextIcon },
        { name: 'Grade Submissions', href: '/grades', icon: ChartBarIcon },
        { name: 'Take Attendance', href: '/attendance', icon: CheckCircleIcon },
        { name: 'Upload Resources', href: '/resources', icon: BookOpenIcon },
      ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-gray-900">
            {user?.role === 'student' ? 'Student Dashboard' : user?.role === 'teacher' ? 'Teacher Dashboard' : 'Admin Dashboard'}
          </h1>
          <p className="mt-1 text-sm text-gray-500">
            Welcome back, {user?.firstName}! Here's what's happening today.
          </p>
        </div>
        <div className="flex items-center space-x-3">
          <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium ${
            user?.role === 'student' ? 'bg-blue-100 text-blue-800' :
            user?.role === 'teacher' ? 'bg-green-100 text-green-800' :
            'bg-purple-100 text-purple-800'
          }`}>
            {user?.role?.charAt(0).toUpperCase()}{user?.role?.slice(1)}
          </span>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 gap-5 mt-5 sm:grid-cols-2 lg:grid-cols-4">
        {stats.map((stat) => (
          <div
            key={stat.name}
            className="px-4 py-5 overflow-hidden bg-white rounded-lg shadow sm:p-6"
          >
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <stat.icon className="h-6 w-6 text-gray-400" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <p className="text-sm font-medium text-gray-500 truncate">{stat.name}</p>
                <div className="flex items-baseline">
                  <p className="text-2xl font-semibold text-gray-900">{stat.value}</p>
                  <span
                    className={`ml-2 text-sm font-medium ${
                      stat.changeType === 'positive' ? 'text-green-600' : 
                      stat.changeType === 'negative' ? 'text-red-600' : 'text-gray-500'
                    }`}
                  >
                    {stat.change}
                  </span>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 gap-6 mt-6 lg:grid-cols-3">
        {/* Upcoming Classes */}
        <div className="lg:col-span-2">
          <div className="overflow-hidden bg-white shadow sm:rounded-lg">
            <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
              <div className="flex items-center justify-between">
                <h2 className="text-lg font-medium leading-6 text-gray-900">Today's Schedule</h2>
                <Link to="/schedule" className="text-sm font-medium text-primary-600 hover:text-primary-500">
                  View all
                </Link>
              </div>
            </div>
            <ul className="divide-y divide-gray-200">
              {upcomingClasses.map((classItem) => (
                <li key={classItem.id} className="px-6 py-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div className={`flex items-center justify-center flex-shrink-0 w-12 h-12 rounded-md ${
                        classItem.type === 'lecture' ? 'bg-blue-100 text-blue-600' : 'bg-green-100 text-green-600'
                      }`}>
                        {classItem.type === 'lecture' ? (
                          <AcademicCapIcon className="w-6 h-6" />
                        ) : (
                          <BookOpenIcon className="w-6 h-6" />
                        )}
                      </div>
                      <div className="ml-4">
                        <p className="text-sm font-medium text-gray-900">{classItem.name}</p>
                        <p className="text-sm text-gray-500">{classItem.time}</p>
                        <p className="text-sm text-gray-500">{classItem.location} â€¢ {classItem.instructor}</p>
                      </div>
                    </div>
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      classItem.type === 'lecture' ? 'bg-blue-100 text-blue-800' : 'bg-green-100 text-green-800'
                    }`}>
                      {classItem.type}
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          </div>

          {/* Upcoming Assignments */}
          {user?.role === 'student' && (
            <div className="mt-6 overflow-hidden bg-white shadow sm:rounded-lg">
              <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
                <div className="flex items-center justify-between">
                  <h2 className="text-lg font-medium leading-6 text-gray-900">Upcoming Assignments</h2>
                  <Link to="/assignments" className="text-sm font-medium text-primary-600 hover:text-primary-500">
                    View all
                  </Link>
                </div>
              </div>
              <ul className="divide-y divide-gray-200">
                {mockAssignments.slice(0, 3).map((assignment) => {
                  const course = mockCourses.find(c => c.id === assignment.courseId);
                  const daysUntilDue = Math.ceil((new Date(assignment.dueDate).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24));

                  return (
                    <li key={assignment.id} className="px-6 py-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div className="flex items-center justify-center flex-shrink-0 w-12 h-12 rounded-md bg-orange-100 text-orange-600">
                            <DocumentTextIcon className="w-6 h-6" />
                          </div>
                          <div className="ml-4">
                            <p className="text-sm font-medium text-gray-900">{assignment.title}</p>
                            <p className="text-sm text-gray-500">{course?.name}</p>
                            <p className="text-sm text-gray-500">Due: {new Date(assignment.dueDate).toLocaleDateString()}</p>
                          </div>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            daysUntilDue < 0 ? 'bg-red-100 text-red-800' :
                            daysUntilDue <= 1 ? 'bg-yellow-100 text-yellow-800' :
                            'bg-green-100 text-green-800'
                          }`}>
                            {daysUntilDue < 0 ? 'Overdue' : daysUntilDue <= 1 ? 'Due Soon' : `${daysUntilDue} days`}
                          </span>
                          <span className="text-sm text-gray-500">{assignment.maxPoints} pts</span>
                        </div>
                      </div>
                    </li>
                  );
                })}
              </ul>
            </div>
          )}
        </div>

        {/* Quick Links */}
        <div>
          <div className="overflow-hidden bg-white shadow sm:rounded-lg">
            <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
              <h2 className="text-lg font-medium leading-6 text-gray-900">Quick Links</h2>
            </div>
            <div className="px-6 py-5">
              <div className="grid grid-cols-2 gap-4">
                {quickLinks.map((item) => (
                  <Link
                    key={item.name}
                    to={item.href}
                    className="flex flex-col items-center justify-center p-4 text-center rounded-md hover:bg-gray-50"
                  >
                    <item.icon className="w-8 h-8 text-primary-600" aria-hidden="true" />
                    <span className="mt-2 text-sm font-medium text-gray-700">{item.name}</span>
                  </Link>
                ))}
              </div>
            </div>
          </div>

          {/* Recent Announcements */}
          <div className="mt-6 overflow-hidden bg-white shadow sm:rounded-lg">
            <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
              <h2 className="text-lg font-medium leading-6 text-gray-900">Announcements</h2>
            </div>
            <ul className="divide-y divide-gray-200">
              {mockAnnouncements.slice(0, 3).map((announcement) => (
                <li key={announcement.id} className="px-6 py-4">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <p className="text-sm font-medium text-gray-900">{announcement.title}</p>
                      <p className="mt-1 text-sm text-gray-500">{announcement.content.substring(0, 100)}...</p>
                      <p className="mt-2 text-xs text-gray-400">
                        {new Date(announcement.publishDate).toLocaleDateString()}
                      </p>
                    </div>
                    <span className={`ml-3 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      announcement.priority === 'high' ? 'bg-red-100 text-red-800' :
                      announcement.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {announcement.priority}
                    </span>
                  </div>
                </li>
              ))}
              <li className="px-6 py-4 text-center">
                <a href="#" className="text-sm font-medium text-primary-600 hover:text-primary-500">
                  View all announcements
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
