import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Fragment } from 'react';
import { Menu, Transition } from '@headlessui/react';
import { Bars3Icon, BellIcon, XMarkIcon, AdjustmentsHorizontalIcon, MagnifyingGlassIcon } from '@heroicons/react/24/outline';
import { useState } from 'react';
import { AuthProvider, useAuth } from './context/AuthContext';
import { ThemeProvider, useTheme } from './context/ThemeContext';
import AccessibilityPanel from './components/AccessibilityPanel';
import FloatingActionButton from './components/FloatingActionButton';
import GlassmorphismSidebar from './components/GlassmorphismSidebar';
import Dashboard from './pages/Dashboard';
import Courses from './pages/Courses';
import Schedule from './pages/Schedule';
import Grades from './pages/Grades';
import Profile from './pages/Profile';
import Login from './pages/Login';
import Assignments from './pages/Assignments';
import Messages from './pages/Messages';
import Attendance from './pages/Attendance';
import Resources from './pages/Resources';
import Exams from './pages/Exams';
import Reports from './pages/Reports';
import Admin from './pages/Admin';

const getNavigationForRole = (role: string) => {
  const baseNav = [
    { name: 'Dashboard', href: '/', current: true },
    { name: 'Courses', href: '/courses', current: false },
    { name: 'Schedule', href: '/schedule', current: false },
  ];

  if (role === 'student') {
    return [
      ...baseNav,
      { name: 'Assignments', href: '/assignments', current: false },
      { name: 'Exams', href: '/exams', current: false },
      { name: 'Grades', href: '/grades', current: false },
      { name: 'Attendance', href: '/attendance', current: false },
      { name: 'Resources', href: '/resources', current: false },
      { name: 'Messages', href: '/messages', current: false },
    ];
  }

  if (role === 'teacher') {
    return [
      ...baseNav,
      { name: 'Assignments', href: '/assignments', current: false },
      { name: 'Exams', href: '/exams', current: false },
      { name: 'Grades', href: '/grades', current: false },
      { name: 'Attendance', href: '/attendance', current: false },
      { name: 'Resources', href: '/resources', current: false },
      { name: 'Messages', href: '/messages', current: false },
      { name: 'Reports', href: '/reports', current: false },
    ];
  }

  if (role === 'admin') {
    return [
      ...baseNav,
      { name: 'Admin Panel', href: '/admin', current: false },
      { name: 'Reports', href: '/reports', current: false },
    ];
  }

  return baseNav;
};

function classNames(...classes: string[]) {
  return classes.filter(Boolean).join(' ');
}

function AppContent() {
  const { user, logout, isAuthenticated } = useAuth();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [accessibilityOpen, setAccessibilityOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  if (!isAuthenticated || !user) {
    return <Login />;
  }

  const navigation = getNavigationForRole(user.role);

  const getRoleBackground = (role: string) => {
    switch (role) {
      case 'student':
        return 'bg-gradient-to-br from-student-50 via-blue-50 to-white';
      case 'teacher':
        return 'bg-gradient-to-br from-teacher-50 via-orange-50 to-white';
      case 'admin':
        return 'bg-gradient-to-br from-admin-50 via-purple-50 to-white';
      default:
        return 'bg-gray-50';
    }
  };

  return (
      <div className={`min-h-screen ${getRoleBackground(user?.role || 'student')}`}>
        {/* Mobile sidebar */}
        <Transition.Root show={sidebarOpen} as={Fragment}>
          <div className="fixed inset-0 z-40 flex md:hidden">
            <Transition.Child
              as={Fragment}
              enter="transition-opacity ease-linear duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="transition-opacity ease-linear duration-300"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setSidebarOpen(false)} />
            </Transition.Child>
            <div className="relative flex flex-col flex-1 w-full max-w-xs bg-white">
              <div className={`flex items-center justify-between h-16 px-4 border-b ${
              user?.role === 'student' ? 'bg-gradient-to-r from-student-500 to-blue-500 border-student-300' :
              user?.role === 'teacher' ? 'bg-gradient-to-r from-teacher-500 to-orange-500 border-teacher-300' :
              'bg-gradient-to-r from-admin-500 to-purple-500 border-admin-300'
            }`}>
                <div className="text-xl font-bold text-white">
                  {user?.role === 'student' ? 'üéì Student Portal' :
                   user?.role === 'teacher' ? 'üë®‚Äçüè´ Teacher Portal' :
                   '‚öôÔ∏è Admin Portal'}
                </div>
                <button
                  type="button"
                  className="inline-flex items-center justify-center w-10 h-10 -mr-2 text-white/80 rounded-md hover:text-white hover:bg-white/10 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white/50"
                  onClick={() => setSidebarOpen(false)}
                >
                  <XMarkIcon className="w-6 h-6" aria-hidden="true" />
                </button>
              </div>
              <nav className="flex-1 px-2 py-4 space-y-2 bg-white">
                {navigation.map((item) => (
                  <a
                    key={item.name}
                    href={item.href}
                    className={classNames(
                      item.current
                        ? `${user?.role === 'student' ? 'bg-student-100 border-student-500 text-student-700' :
                            user?.role === 'teacher' ? 'bg-teacher-100 border-teacher-500 text-teacher-700' :
                            'bg-admin-100 border-admin-500 text-admin-700'}`
                        : 'border-transparent text-gray-600 hover:bg-gray-50 hover:text-gray-900',
                      'group flex items-center px-4 py-3 text-sm font-medium border-l-4 rounded-r-lg transition-all duration-150'
                    )}
                  >
                    {item.name}
                  </a>
                ))}
              </nav>
            </div>
          </div>
        </Transition.Root>

        {/* Static sidebar for desktop */}
        <div className="hidden md:fixed md:inset-y-0 md:flex md:w-64 md:flex-col">
          <div className="flex flex-col flex-1 min-h-0 border-r border-gray-200 bg-white shadow-lg">
            <div className={`flex items-center flex-shrink-0 h-16 px-4 ${
              user?.role === 'student' ? 'bg-gradient-to-r from-student-500 to-blue-500' :
              user?.role === 'teacher' ? 'bg-gradient-to-r from-teacher-500 to-orange-500' :
              'bg-gradient-to-r from-admin-500 to-purple-500'
            }`}>
              <div className="text-xl font-bold text-white">
                {user?.role === 'student' ? 'üéì Student Portal' :
                 user?.role === 'teacher' ? 'üë®‚Äçüè´ Teacher Portal' :
                 '‚öôÔ∏è Admin Portal'}
              </div>
            </div>
            <div className="flex flex-col flex-1 overflow-y-auto">
              <nav className="flex-1 px-2 py-4 space-y-2">
                {navigation.map((item) => (
                  <a
                    key={item.name}
                    href={item.href}
                    className={classNames(
                      item.current
                        ? `${user?.role === 'student' ? 'bg-student-100 border-student-500 text-student-700' :
                            user?.role === 'teacher' ? 'bg-teacher-100 border-teacher-500 text-teacher-700' :
                            'bg-admin-100 border-admin-500 text-admin-700'}`
                        : 'border-transparent text-gray-600 hover:bg-gray-50 hover:text-gray-900',
                      'group flex items-center px-4 py-3 text-sm font-medium border-l-4 rounded-r-lg transition-all duration-150 hover:shadow-sm'
                    )}
                  >
                    {item.name}
                  </a>
                ))}
              </nav>
            </div>
          </div>
        </div>

        {/* Main content */}
        <div className="flex flex-col md:pl-64">
          <div className="sticky top-0 z-10 bg-white border-b border-gray-200 shadow-sm">
            <div className="flex items-center justify-between h-16 px-4">
              <div className="flex items-center space-x-4">
                <button
                  type="button"
                  className="px-4 py-2 text-gray-500 border border-transparent rounded-md md:hidden focus:outline-none focus:ring-2 focus:ring-inset focus:ring-primary-500"
                  onClick={() => setSidebarOpen(true)}
                >
                  <Bars3Icon className="w-6 h-6" aria-hidden="true" />
                </button>

                {/* Search Bar */}
                <div className="hidden md:block relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                  </div>
                  <input
                    type="text"
                    placeholder={`Search ${user?.role === 'student' ? 'courses, materials, teachers' : 
                                          user?.role === 'teacher' ? 'students, assignments, courses' : 
                                          'users, courses, reports'}...`}
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="block w-80 pl-10 pr-3 py-2 border border-gray-300 rounded-lg leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-primary-500 focus:border-primary-500 text-sm"
                  />
                </div>
              </div>

              <div className="flex items-center space-x-3">
                <button
                  type="button"
                  onClick={() => setAccessibilityOpen(true)}
                  className="p-2 text-gray-400 bg-white rounded-lg hover:text-gray-500 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors duration-150"
                  title="Accessibility Settings"
                >
                  <AdjustmentsHorizontalIcon className="w-5 h-5" aria-hidden="true" />
                </button>

                <button
                  type="button"
                  className="relative p-2 text-gray-400 bg-white rounded-lg hover:text-gray-500 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors duration-150"
                >
                  <BellIcon className="w-5 h-5" aria-hidden="true" />
                  <span className="absolute top-1 right-1 block h-2 w-2 rounded-full bg-red-400 ring-2 ring-white"></span>
                </button>

                {/* Profile dropdown */}
                <Menu as="div" className="relative ml-3">
                  <div>
                    <Menu.Button className="flex items-center max-w-xs text-sm bg-white rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 hover:shadow-md transition-all duration-150">
                      <span className="sr-only">Open user menu</span>
                      {user.avatar ? (
                        <img className="w-9 h-9 rounded-full border-2 border-gray-200" src={user.avatar} alt="" />
                      ) : (
                        <div className={`w-9 h-9 rounded-full flex items-center justify-center font-semibold text-white ${
                          user?.role === 'student' ? 'bg-student-500' :
                          user?.role === 'teacher' ? 'bg-teacher-500' :
                          'bg-admin-500'
                        }`}>
                          {user.firstName[0]}{user.lastName[0]}
                        </div>
                      )}
                    </Menu.Button>
                  </div>
                  <Transition
                    as={Fragment}
                    enter="transition ease-out duration-100"
                    enterFrom="transform opacity-0 scale-95"
                    enterTo="transform opacity-100 scale-100"
                    leave="transition ease-in duration-75"
                    leaveFrom="transform opacity-100 scale-100"
                    leaveTo="transform opacity-0 scale-95"
                  >
                    <Menu.Items className="absolute right-0 w-48 py-1 mt-2 origin-top-right bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none border border-gray-100">
                      <div className="px-4 py-3 border-b border-gray-100">
                        <p className="text-sm font-medium text-gray-900">{user.firstName} {user.lastName}</p>
                        <p className="text-xs text-gray-500">{user.email}</p>
                      </div>
                      <Menu.Item>
                        {({ active }) => (
                          <a
                            href="/profile"
                            className={classNames(
                              active ? 'bg-gray-50' : '',
                              'block px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors duration-150'
                            )}
                          >
                            Your Profile
                          </a>
                        )}
                      </Menu.Item>
                      <Menu.Item>
                        {({ active }) => (
                          <button
                            onClick={logout}
                            className={classNames(
                              active ? 'bg-gray-50' : '',
                              'block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors duration-150'
                            )}
                          >
                            Sign out
                          </button>
                        )}
                      </Menu.Item>
                    </Menu.Items>
                  </Transition>
                </Menu>
              </div>
            </div>
          </div>

          <main className="flex-1">
            <div className="py-8">
              <div className="px-6 mx-auto max-w-7xl sm:px-8 md:px-10">
                <Routes>
                  <Route path="/" element={<Dashboard />} />
                  <Route path="/courses" element={<Courses />} />
                  <Route path="/schedule" element={<Schedule />} />
                  <Route path="/assignments" element={<Assignments />} />
                  <Route path="/exams" element={<Exams />} />
                  <Route path="/grades" element={<Grades />} />
                  <Route path="/attendance" element={<Attendance />} />
                  <Route path="/resources" element={<Resources />} />
                  <Route path="/messages" element={<Messages />} />
                  <Route path="/reports" element={<Reports />} />
                  <Route path="/admin" element={<Admin />} />
                  <Route path="/profile" element={<Profile />} />
                  <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>
              </div>
            </div>
          </main>
        </div>

        <AccessibilityPanel 
          isOpen={accessibilityOpen} 
          onClose={() => setAccessibilityOpen(false)} 
        />

        <FloatingActionButton userRole={user?.role || 'student'} />
      </div>
  );
}

export default function App() {
  return (
    <ThemeProvider>
      <AuthProvider>
        <AppContent />
      </AuthProvider>
    </ThemeProvider>
  );
}
