import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Fragment } from 'react';
import { Menu, Transition } from '@headlessui/react';
import { Bars3Icon, BellIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { useState } from 'react';
import { AuthProvider, useAuth } from './context/AuthContext';
import Dashboard from './pages/Dashboard';
import Courses from './pages/Courses';
import Schedule from './pages/Schedule';
import Grades from './pages/Grades';
import Profile from './pages/Profile';
import Login from './pages/Login';
import Assignments from './pages/Assignments';
import Messages from './pages/Messages';
import Attendance from './pages/Attendance';
import Resources from './pages/Resources';
import Exams from './pages/Exams';
import Reports from './pages/Reports';
import Admin from './pages/Admin';

const getNavigationForRole = (role: string) => {
  const baseNav = [
    { name: 'Dashboard', href: '/', current: true },
    { name: 'Courses', href: '/courses', current: false },
    { name: 'Schedule', href: '/schedule', current: false },
  ];

  if (role === 'student') {
    return [
      ...baseNav,
      { name: 'Assignments', href: '/assignments', current: false },
      { name: 'Exams', href: '/exams', current: false },
      { name: 'Grades', href: '/grades', current: false },
      { name: 'Attendance', href: '/attendance', current: false },
      { name: 'Resources', href: '/resources', current: false },
      { name: 'Messages', href: '/messages', current: false },
    ];
  }

  if (role === 'teacher') {
    return [
      ...baseNav,
      { name: 'Assignments', href: '/assignments', current: false },
      { name: 'Exams', href: '/exams', current: false },
      { name: 'Grades', href: '/grades', current: false },
      { name: 'Attendance', href: '/attendance', current: false },
      { name: 'Resources', href: '/resources', current: false },
      { name: 'Messages', href: '/messages', current: false },
      { name: 'Reports', href: '/reports', current: false },
    ];
  }

  if (role === 'admin') {
    return [
      ...baseNav,
      { name: 'Admin Panel', href: '/admin', current: false },
      { name: 'Reports', href: '/reports', current: false },
    ];
  }

  return baseNav;
};

function classNames(...classes: string[]) {
  return classes.filter(Boolean).join(' ');
}

function AppContent() {
  const { user, logout, isAuthenticated } = useAuth();
  const [sidebarOpen, setSidebarOpen] = useState(false);

  if (!isAuthenticated || !user) {
    return <Login />;
  }

  const navigation = getNavigationForRole(user.role);

  return (
      <div className="min-h-screen bg-gray-50">
        {/* Mobile sidebar */}
        <Transition.Root show={sidebarOpen} as={Fragment}>
          <div className="fixed inset-0 z-40 flex md:hidden">
            <Transition.Child
              as={Fragment}
              enter="transition-opacity ease-linear duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="transition-opacity ease-linear duration-300"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setSidebarOpen(false)} />
            </Transition.Child>
            <div className="relative flex flex-col flex-1 w-full max-w-xs bg-white">
              <div className="flex items-center justify-between h-16 px-4 border-b border-gray-200">
                <div className="text-xl font-bold text-primary-600">Student Portal</div>
                <button
                  type="button"
                  className="inline-flex items-center justify-center w-10 h-10 -mr-2 text-gray-500 rounded-md hover:text-gray-900 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-primary-500"
                  onClick={() => setSidebarOpen(false)}
                >
                  <XMarkIcon className="w-6 h-6" aria-hidden="true" />
                </button>
              </div>
              <nav className="flex-1 px-2 py-4 space-y-1 bg-white">
                {navigation.map((item) => (
                  <a
                    key={item.name}
                    href={item.href}
                    className={classNames(
                      item.current
                        ? 'bg-primary-50 border-primary-500 text-primary-700'
                        : 'border-transparent text-gray-600 hover:bg-gray-50 hover:text-gray-900',
                      'group flex items-center px-3 py-2 text-sm font-medium border-l-4 rounded-r-md'
                    )}
                  >
                    {item.name}
                  </a>
                ))}
              </nav>
            </div>
          </div>
        </Transition.Root>

        {/* Static sidebar for desktop */}
        <div className="hidden md:fixed md:inset-y-0 md:flex md:w-64 md:flex-col">
          <div className="flex flex-col flex-1 min-h-0 border-r border-gray-200 bg-white">
            <div className="flex items-center flex-shrink-0 h-16 px-4 bg-white">
              <div className="text-xl font-bold text-primary-600">Student Portal</div>
            </div>
            <div className="flex flex-col flex-1 overflow-y-auto">
              <nav className="flex-1 px-2 py-4 space-y-1">
                {navigation.map((item) => (
                  <a
                    key={item.name}
                    href={item.href}
                    className={classNames(
                      item.current
                        ? 'bg-primary-50 border-primary-500 text-primary-700'
                        : 'border-transparent text-gray-600 hover:bg-gray-50 hover:text-gray-900',
                      'group flex items-center px-3 py-2 text-sm font-medium border-l-4 rounded-r-md'
                    )}
                  >
                    {item.name}
                  </a>
                ))}
              </nav>
            </div>
          </div>
        </div>

        {/* Main content */}
        <div className="flex flex-col md:pl-64">
          <div className="sticky top-0 z-10 flex items-center justify-between h-16 px-4 bg-white border-b border-gray-200 shadow-sm">
            <button
              type="button"
              className="px-4 py-2 text-gray-500 border border-transparent rounded-md md:hidden focus:outline-none focus:ring-2 focus:ring-inset focus:ring-primary-500"
              onClick={() => setSidebarOpen(true)}
            >
              <Bars3Icon className="w-6 h-6" aria-hidden="true" />
            </button>

            <div className="flex items-center">
              <button
                type="button"
                className="p-1 text-gray-400 bg-white rounded-full hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
              >
                <BellIcon className="w-6 h-6" aria-hidden="true" />
              </button>

              {/* Profile dropdown */}
              <Menu as="div" className="relative ml-3">
                <div>
                  <Menu.Button className="flex items-center max-w-xs text-sm bg-white rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500">
                    <span className="sr-only">Open user menu</span>
                    {user.avatar ? (
                      <img className="w-8 h-8 rounded-full" src={user.avatar} alt="" />
                    ) : (
                      <div className="w-8 h-8 rounded-full bg-primary-100 flex items-center justify-center text-primary-700 font-medium">
                        {user.firstName[0]}{user.lastName[0]}
                      </div>
                    )}
                  </Menu.Button>
                </div>
                <Transition
                  as={Fragment}
                  enter="transition ease-out duration-100"
                  enterFrom="transform opacity-0 scale-95"
                  enterTo="transform opacity-100 scale-100"
                  leave="transition ease-in duration-75"
                  leaveFrom="transform opacity-100 scale-100"
                  leaveTo="transform opacity-0 scale-95"
                >
                  <Menu.Items className="absolute right-0 w-48 py-1 mt-2 origin-top-right bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
                    <Menu.Item>
                      {({ active }) => (
                        <a
                          href="/profile"
                          className={classNames(
                            active ? 'bg-gray-100' : '',
                            'block px-4 py-2 text-sm text-gray-700'
                          )}
                        >
                          Your Profile
                        </a>
                      )}
                    </Menu.Item>
                    <Menu.Item>
                      {({ active }) => (
                        <button
                          onClick={logout}
                          className={classNames(
                            active ? 'bg-gray-100' : '',
                            'block w-full text-left px-4 py-2 text-sm text-gray-700'
                          )}
                        >
                          Sign out
                        </button>
                      )}
                    </Menu.Item>
                  </Menu.Items>
                </Transition>
              </Menu>
            </div>
          </div>

          <main className="flex-1">
            <div className="py-6">
              <div className="px-4 mx-auto max-w-7xl sm:px-6 md:px-8">
                <Routes>
                  <Route path="/" element={<Dashboard />} />
                  <Route path="/courses" element={<Courses />} />
                  <Route path="/schedule" element={<Schedule />} />
                  <Route path="/assignments" element={<Assignments />} />
                  <Route path="/exams" element={<Exams />} />
                  <Route path="/grades" element={<Grades />} />
                  <Route path="/attendance" element={<Attendance />} />
                  <Route path="/resources" element={<Resources />} />
                  <Route path="/messages" element={<Messages />} />
                  <Route path="/reports" element={<Reports />} />
                  <Route path="/admin" element={<Admin />} />
                  <Route path="/profile" element={<Profile />} />
                  <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>
              </div>
            </div>
          </main>
        </div>
      </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}
