import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import { ThemeProvider } from './context/ThemeContext';
import AccessibilityPanel from './components/AccessibilityPanel';
import FloatingActionButton from './components/FloatingActionButton';
import GlassmorphismSidebar from './components/GlassmorphismSidebar';
import Dashboard from './pages/Dashboard';
import Courses from './pages/Courses';
import Schedule from './pages/Schedule';
import Grades from './pages/Grades';
import Profile from './pages/Profile';
import Login from './pages/Login';
import Assignments from './pages/Assignments';
import Messages from './pages/Messages';
import Attendance from './pages/Attendance';
import Resources from './pages/Resources';
import Exams from './pages/Exams';
import Reports from './pages/Reports';
import Admin from './pages/Admin';

function AppContent() {
  const { user, isAuthenticated } = useAuth();
  const [accessibilityOpen, setAccessibilityOpen] = useState(false);

  if (!isAuthenticated || !user) {
    return <Login />;
  }

  const getRoleBackground = (role: string) => {
    switch (role) {
      case 'student':
        return 'bg-gradient-to-br from-student-50 via-blue-50 to-white';
      case 'teacher':
        return 'bg-gradient-to-br from-teacher-50 via-orange-50 to-white';
      case 'admin':
        return 'bg-gradient-to-br from-admin-50 via-purple-50 to-white';
      default:
        return 'bg-gray-50';
    }
  };

  return (
    <div className={`min-h-screen ${getRoleBackground(user?.role || 'student')}`}>
      <div className="flex h-screen">
        {/* Glassmorphism Sidebar */}
        <GlassmorphismSidebar />

        {/* Main content with left margin for sidebar */}
        <div className="flex flex-col ml-80 flex-1">
          <div className={`flex items-center flex-shrink-0 h-16 px-4 ${
            user?.role === 'student' ? 'bg-gradient-to-r from-student-500 to-blue-500' :
            user?.role === 'teacher' ? 'bg-gradient-to-r from-teacher-500 to-orange-500' :
            'bg-gradient-to-r from-admin-500 to-purple-500'
          }`}>
            <div className="text-xl font-bold text-white">
              {user?.role === 'student' ? 'üéì Student Portal' :
               user?.role === 'teacher' ? 'üë®‚Äçüè´ Teacher Portal' :
               '‚öôÔ∏è Admin Portal'}
                {user?.role === 'student' ? 'üéì Student Portal' :
                 user?.role === 'teacher' ? 'üë®‚Äçüè´ Teacher Portal' :
                 '‚öôÔ∏è Admin Portal'}
              </div>
            </div>
            <div className="flex flex-col flex-1 overflow-y-auto">
              <nav className="flex-1 px-2 py-4 space-y-2">
                {navigation.map((item) => (
                  <a
                    key={item.name}
                    href={item.href}
                    className={classNames(
                      item.current
                        ? `${user?.role === 'student' ? 'bg-student-100 border-student-500 text-student-700' :
                            user?.role === 'teacher' ? 'bg-teacher-100 border-teacher-500 text-teacher-700' :
                            'bg-admin-100 border-admin-500 text-admin-700'}`
                        : 'border-transparent text-gray-600 hover:bg-gray-50 hover:text-gray-900',
                      'group flex items-center px-4 py-3 text-sm font-medium border-l-4 rounded-r-lg transition-all duration-150 hover:shadow-sm'
                    )}
                  >
                    {item.name}
                  </a>
                ))}
              </nav>
            </div>
          </div>
        </div>

        {/* Main content */}
        <div className="flex flex-col md:pl-64">
          <div className="sticky top-0 z-10 bg-white border-b border-gray-200 shadow-sm">
            <div className="flex items-center justify-between h-16 px-4">
              <div className="flex items-center space-x-4">
                <button
                  type="button"
                  className="px-4 py-2 text-gray-500 border border-transparent rounded-md md:hidden focus:outline-none focus:ring-2 focus:ring-inset focus:ring-primary-500"
                  onClick={() => setSidebarOpen(true)}
                >
                  <Bars3Icon className="w-6 h-6" aria-hidden="true" />
                </button>

                {/* Search Bar */}
                <div className="hidden md:block relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                  </div>
                  <input
                    type="text"
                    placeholder={`Search ${user?.role === 'student' ? 'courses, materials, teachers' : 
                                          user?.role === 'teacher' ? 'students, assignments, courses' : 
                                          'users, courses, reports'}...`}
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="block w-80 pl-10 pr-3 py-2 border border-gray-300 rounded-lg leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-primary-500 focus:border-primary-500 text-sm"
                  />
                </div>
              </div>

              <div className="flex items-center space-x-3">
                <button
                  type="button"
                  onClick={() => setAccessibilityOpen(true)}
                  className="p-2 text-gray-400 bg-white rounded-lg hover:text-gray-500 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors duration-150"
                  title="Accessibility Settings"
                >
                  <AdjustmentsHorizontalIcon className="w-5 h-5" aria-hidden="true" />
                </button>

                <button
                  type="button"
                  className="relative p-2 text-gray-400 bg-white rounded-lg hover:text-gray-500 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors duration-150"
                >
                  <BellIcon className="w-5 h-5" aria-hidden="true" />
