                    <Menu.Button className="flex items-center max-w-xs text-sm bg-white rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 hover:shadow-md transition-all duration-150">
                      <span className="sr-only">Open user menu</span>
                      {user.avatar ? (
                        <img className="w-9 h-9 rounded-full border-2 border-gray-200" src={user.avatar} alt="" />
                      ) : (
                        <div className={`w-9 h-9 rounded-full flex items-center justify-center font-semibold text-white ${
                          user?.role === 'student' ? 'bg-student-500' :
                          user?.role === 'teacher' ? 'bg-teacher-500' :
                          'bg-admin-500'
                        }`}>
                          {user.firstName[0]}{user.lastName[0]}
                        </div>
                      )}
                    </Menu.Button>
                  </div>
                  <Transition
                    as={Fragment}
                    enter="transition ease-out duration-100"
                    enterFrom="transform opacity-0 scale-95"
                    enterTo="transform opacity-100 scale-100"
                    leave="transition ease-in duration-75"
                    leaveFrom="transform opacity-100 scale-100"
                    leaveTo="transform opacity-0 scale-95"
                  >
                    <Menu.Items className="absolute right-0 w-48 py-1 mt-2 origin-top-right bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none border border-gray-100">
                      <div className="px-4 py-3 border-b border-gray-100">
                        <p className="text-sm font-medium text-gray-900">{user.firstName} {user.lastName}</p>
                        <p className="text-xs text-gray-500">{user.email}</p>
                      </div>
                      <Menu.Item>
                        {({ active }) => (
                          <a
                            href="/profile"
                            className={classNames(
                              active ? 'bg-gray-50' : '',
                              'block px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors duration-150'
                            )}
                          >
                            Your Profile
                          </a>
                        )}
                      </Menu.Item>
                      <Menu.Item>
                        {({ active }) => (
                          <button
                            onClick={logout}
                            className={classNames(
                              active ? 'bg-gray-50' : '',
                              'block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors duration-150'
                            )}
                          >
                            Sign out
                          </button>
                        )}
                      </Menu.Item>
                    </Menu.Items>
                  </Transition>
                </Menu>
              </div>
            </div>
          </div>

          <main className="flex-1">
            <div className="py-8">
              <div className="px-6 mx-auto max-w-7xl sm:px-8 md:px-10">
                <Routes>
                  <Route path="/" element={<Dashboard />} />
                  <Route path="/courses" element={<Courses />} />
                  <Route path="/schedule" element={<Schedule />} />
                  <Route path="/assignments" element={<Assignments />} />
                  <Route path="/exams" element={<Exams />} />
                  <Route path="/grades" element={<Grades />} />
                  <Route path="/attendance" element={<Attendance />} />
                  <Route path="/resources" element={<Resources />} />
                  <Route path="/messages" element={<Messages />} />
                  <Route path="/reports" element={<Reports />} />
                  <Route path="/admin" element={<Admin />} />
                  <Route path="/profile" element={<Profile />} />
                  <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>
              </div>
            </div>
          </main>
        </div>

        <AccessibilityPanel 
          isOpen={accessibilityOpen} 
          onClose={() => setAccessibilityOpen(false)} 
        />

        <FloatingActionButton userRole={user?.role || 'student'} />
      </div>
  );
